import './SearchBar.css'
import React,{useState} from 'react';



export const SearchBar = ({ data }) => {
    let [filteredPlayers , setFilteredData] = useState( []);
    const filterPlayerData =(e)=>{
        const searchText=  e.target.value
        console.log(searchText)
        filteredPlayers = data.filter((player)=>{
            return player.Name.includes(searchText);
            console.log(filteredPlayers)
        });

     };
    return (
        <div>
            <div className="search-box">
                <input type='text' placeholder="Search for player" onChange={filterPlayerData}/>
            </div>
            <div className="search-result">
                {filteredPlayers.map((value, key) => {
                    return <a className="player-data" href="">
                        <p>{value.Name}</p>
                    </a>
                })}
            </div>

        </div>
    )
}
