import './SearchBar.css'
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, NavLink } from 'react-router-dom';

export const SearchBar = ({ data }) => {
    const [filteredData, setFilteredData] = useState([]);
    const [textenterd, settextenterd] = useState("");

    const filterData = (event) => {
        const searchText = event.target.value;
        settextenterd(searchText);
        const newFilter = data.filter((player) => {
            const playerName = player.Name.toLowerCase();
            const playerNationality = player.Nationality.toLowerCase();
            const playerPosition = player.Position.toLowerCase();
            const playerAge = player.Age.toString();
            const preferredFoot = player['Preferred Foot'].toLowerCase();
            if (playerNationality.includes(searchText.toLowerCase()) ||
                playerName.includes(searchText.toLowerCase()) ||
                playerAge.includes(searchText)||
                playerPosition.includes(searchText.toLowerCase()) ||
                preferredFoot.includes(searchText.toLowerCase())) {
                return player;

            }
        });

        if (searchText === "") {
            setFilteredData([]);
        } else {
            setFilteredData(newFilter);
        }
    };
    return (
        <Router>
           <div>
           <img src={'https://upload.wikimedia.org/wikipedia/commons/thumb/a/af/FIFA_series_logo_%282010%E2%80%932020%29.svg/2560px-FIFA_series_logo_%282010%E2%80%932020%29.svg.png'} width='400' height='200' />


            <div className="search-container">
            <div className="search-box">
                <input className="search-input" type='text' placeholder="Search for player" onChange={filterData} />
            </div>
            { 
                filteredData.length != 0 && (


                    <div className="search-result">
                        {filteredData.map((value, key) => {
                            return (<NavLink exact to={`/PlayerProfile/${value.Name}`}>
                                <p className="player-data-item" key={value.id} >{value.Name}</p>
                            </NavLink>)
                        })}
                    </div>
                )
            }

        </div>
           </div>
        </Router>
    )
}
