import React,{useState} from 'react';
const[ searchtext, setsearchtext]  = useState('')




export const SearchBar = ({ data }) => {

    const filterPlayers = (PlayersData, query) => {
        if (!query) {
          return PlayersData;

        }

        return PlayersData.filter((PlayerData) => {
          const playerName = PlayerData.Name.toLowerCase();
          return playerName.includes(query)


        });
      };

    return (
        <div>
            <div className="search-box">
                <input type='text' placeholder="Search for player"  onChange={event=>{setsearchtext(event.target.value)
                filterPlayers(data,setsearchtext)
                }}/>
            </div>
            <div className="search-result">
                {filteredPlayers.map((value, key) => {
                    return (<a className="player-data" href="">
                    <p>{value.Name}</p>
                </a>)
                })}
            </div>

        </div>
    )
}
