import './SearchBar.css'
import React, { useState } from 'react';

export const SearchBar = ({ data }) => {
    const [filteredData, setFilteredData] = useState([]);
    const [textenterd, settextenterd] = useState("");

    const filterData = (event) => {
        const searchText = event.target.value;
        settextenterd(searchText);
        const newFilter = data.filter((value) => {
            const playerName = value.Name.toLowerCase();
            const playerNationality = value.Nationality.toLowerCase();
            const playerPosition = value.Position.toLowerCase();
            const preferredFoot = value['Preferred Foot'].toLowerCase();
            if (playerNationality.includes(searchText.toLowerCase()) ||
                playerName.includes(searchText.toLowerCase()) ||
                playerPosition.includes(searchText.toLowerCase()) ||
                preferredFoot.includes(searchText.toLowerCase())) {
                return value;

            }
        });

        /* if (searchText === "") {
            setFilteredData([]);
        } else {
            setFilteredData(newFilter);
        } */
    };



    return (
        <div>
            <div className="search-box">
                <input type='text' placeholder="Search for player" onChange={filterData} />
            </div>
            {
                filteredData.length != 0 && (
                    <div className="search-result">
                        {filteredData.map((value, key) => {
                            return (<a className="player-data" href="">
                                <p>{value.Name}</p>
                            </a>)
                        })}
                    </div>
                )
            }

        </div>
    )
}
